const positions = [
  {
    company: "Flipkart",
    position: "Summer Intern",
    link: "https://flipkart.com",
    daterange: "May 2021 - July 2021",
    points: [
      "Reduced the potential bad burn incurred by the company during major sale events by enhancing the anomaly detection system",
      "Prepared comprehensive reports to perform real-time analysis of the modifications made to the anomaly detection system",
    ],
  },
  {
    company: "Newzera",
    position: "Winter Intern",
    link: "https://newzera.com",
    daterange: "January 2021 - March 2021",
    points: [
      "Developed the server-side architecture of Content Management System using GraphQL with Apollo Client.",
      "Developed and designed interactive front-end of various internal applications used by the company using React.js",
      "Tested various softwares to ensure appropriate responsiveness and effectiveness",
    ],
  },
  {
    company: "Helix Tech.",
    position: "Software Development Intern",
    link: "https://www.helixtech.co/",
    daterange: "May 2019 - July 2019",
    points: [
